import * as React from 'react';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';

const columns = [
  {
    id: 'asset',
    label: 'Asset',
    minWidth: 170,
  },
  {
    id: 'status',
    label: 'Status',
    minWidth: 100
  },
  {
    id: 'dateModified',
    label: 'Date last Modified',
    minWidth: 170,
    align: 'right',
  },
  {
    id: 'employee',
    label: 'Employee',
    minWidth: 170,
    align: 'right',
  },
  {
    id: 'notes',
    label: 'Notes',
    minWidth: 170,
    align: 'right',
  },
  {
    id: 'modify',
    label: 'Modify',
    minWidth: 170,
    align: 'right',
  },
];

function createData(asset, status, dateModified, employee, notes, modify) {
  return { asset, status, dateModified, employee, notes, modify };
}

const rows = [
  createData('Tractor', 'IN-SERVICE', '02/12/2022', 'J.F'),
  createData('Tractor', 'IN-SERVICE', '02/12/2022', 'J.F'),
  createData('Trailer', 'IN-SERVICE', '02/12/2022', 'J.F'),
  createData('Trailer', 'OUT-OF-SERVICE', '02/12/2022', 'J.F'),
  createData('Trailer', 'IN-SERVICE', '02/12/2022', 'J.F'),
  createData('Tractor', 'OUT-OF-SERVICE', '02/12/2022', 'J.F'),
  createData('Tractor', 'IN-SERVICE', '02/12/2022', 'J.F'),
  createData('Trailer', 'OUT-OF-SERVICE', '02/12/2022', 'J.F'),
  createData('Tractor', 'OUT-OF-SERVICE', '02/12/2022', 'J.F'),
  createData('Trailer', 'IN-SERVICE', '02/12/2022', 'J.F'),
  createData('Trailer', 'IN-SERVICE', '02/12/2022', 'J.F'),
  createData('Trailer', 'IN-SERVICE', '02/12/2022', 'J.F'),
  createData('Trailer', 'OUT-OF-SERVICE', '02/12/2022', 'J.F'),
  createData('Tractor', 'IN-SERVICE', '02/12/2022', 'J.F'),
  createData('Tractor', 'IN-SERVICE', '02/12/2022', 'J.F'),
];

export default function StickyHeadTable() {
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  return (
      <Paper sx={{ width: '100%', overflow: 'hidden' }}>
        <TableContainer sx={{ maxHeight: 440 }}>
          <Table stickyHeader aria-label="sticky table">
            <TableHead>
              <TableRow>
                {columns.map((column) => (
                    <TableCell
                        key={column.id}
                        align={column.align}
                        style={{
                          minWidth: column.minWidth,
                          backgroundColor: "lightgrey",
                          textDecoration: "underline",
                        }}
                    >
                      {column.label}
                    </TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {rows
                  .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                  .map((row) => {
                    return (
                        <TableRow hover role="checkbox" tabIndex={-1} key={row.status}>
                          {columns.map((column) => {
                            const value = row[column.id];
                            return (
                                <TableCell key={column.id} align={column.align}>
                                  {column.format && typeof value === 'number'
                                      ? column.format(value)
                                      : value}
                                </TableCell>
                            );
                          })}
                        </TableRow>
                    );
                  })}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
            rowsPerPageOptions={[5, 10, 25, 100]}
            component="div"
            count={rows.length}
            rowsPerPage={rowsPerPage}
            page={page}
            onPageChange={handleChangePage}
            onRowsPerPageChange={handleChangeRowsPerPage}
        />
      </Paper>
  );
}
